commits.content:
.restaurant_card{
    background-color:$colour-white;
    margin: auto;
    margin-bottom: 30px;
    position: relative;
    width: 92%;
    box-shadow: rgba(24, 24, 24, 0.35) 0px 5px 15px;
    border-radius: 15px;
    padding-bottom: 15px; 
    img{
        @include img_size;
    }
    .green_card{
        background-color: $colour-tertiary;
        color: darken($colour-tertiary, 35%);
        font-weight: 700;
        position: absolute;
        right : 2%;
        top : 5%;
        padding: 10px;
        border-radius: 5%;
    }
    .description{
        position: relative;
        h3, p{
            line-height: 1.5;
        }
    }
}
