commits.content:
header{
    position: relative;
    a{
        position: absolute;
        left: 10px;
    }
}
.image img{
    @include img_size;
    border-radius: 0px;
    height: 300px;
}
.main2{
    margin-top: -60px;
    padding: 20px 0px;
    background-color: $colour-gray;
    border-radius: 50px 50px 0 0 ;
    position: relative;
    .restaurant_name{
        font-size: 62%;
        h1{
            width: 300px;
            margin: auto;
        }
        .heart .fas{
            top: 35px;
            right: 30px;
            animation: fill-heart all 1s ease-in-out;
        }
        }
    } 
    .menu{
        display: flex;
        flex-direction: column;
        justify-content: space-evenly;
        width: 100%;
        //.entrees .plats .desserts{
            width: 100%;
        }
        .entrees .course{
            animation : loading_menus 3s;
            animation-fill-mode: forwards;
        }
        .plats .course{
            animation : loading_menus 6s;
            animation-fill-mode: forwards;
        }
        .desserts .course{
            animation : loading_menus 9s;
            animation-fill-mode: forwards;
        }
        @include media_tablet{
            flex-direction: column;
            .course{
                width: 500px;
                    .dish{
                        width: 85%;
                    }
            }
        }
        @include media_desktop{
            flex-direction: row;
            .entrees, .plats, .desserts{
                width: 30%;
            .course{
                width: auto;
            .dish{
                width: 85%;
                }
            }
        }
    }
    }
    a.commander{
        width: 300px;
        margin: 50px auto;
    }
