commits.content:
main{

  form{
    @include flexTool(row);
    height: 55px;
    background-color: darken($colour-gray, 5);
    border: none;
    box-shadow: inset 0px 10px 5px -6px darken($colour-gray, 20);
    margin: auto;
    margin-bottom: 0;

  }       
  input, label{
    border: none;
    background-color: transparent;
    font-size: large;
    font-weight: 600;
    padding: 10px;
  }
  input{
    width: 170px;
  }
}
.introduction, .liste_restaurants{
  background-color: $colour-gray;
  padding-bottom: 40px;
}
.introduction{
  @include flexTool(column);
  .introduction__titre{
  width: 90%;
  text-align: center;
  padding-bottom: 40px;     
  }
}
.fonctionnement{
  width: 100%;
  padding: 30px 0px;
  @include media_desktop{
    .grey{
      width: 30%;
    }
    .trois-etapes{
      @include flexTool(row);
    }
  } 
  .trois-etapes{
    background-color: $colour-white;
    position: relative;
    p{
      width: 300px;
      padding-left: 40px;
    }
  }
}
.liste_restaurants{
  padding-top: 20px;

  .liste_cards{
    display: grid;
    grid-template-columns: 1fr;
    @include media_tablet{
